import React, { Component, useEffect } from 'react';
import { appTitle } from '../globals/globals';
import axios from 'axios';
import Loading from './Loading';


export class WorkInfo extends Component {

    state = {
        work: {},
        isLoaded: false,
        pageTitle: ""
    }

    componentDidMount() {
        // document.title= `${this.props.match.params.id} - ${appTitle}`;
        let id ;

        switch(this.props.match.params.id) {
            case 'seoul-alleys':
                id = 13;
                break;
            case 'kine-movie':
                id = 11;
                break;
            case 'the-velou':
                id = 21;
                break;
            default: 
                id = null;
              // code block
        }

        axios.get(`/wp-json/wp/v2/works/${id}`)

        .then(res => this.setState({
            work: res.data,
            isLoaded: true,
            pageTitle: res.data.title.rednered
        }))
        .catch(err => console.log(err));
    }

    render() {
        const { work, isLoaded, pageTitle } = this.state;

        document.title= `${work.title.rendered} - ${appTitle}`;


        if ( isLoaded ) {
            return (
                <div>
                    {/* Hero Section */}
                    <div className="work-hero">
                        <img src={work.acf.hero_bg_image} alt="Page hero" />
                        <h1>{work.title.rendered}</h1>
                        <h1>{pageTitle}</h1>
                    </div>

                    {/* Overview */}
                    <div className="work-overview wrapper">
                        <h2>Overview</h2>
                        <p>{work.acf.overview[0].overview}</p>
                        <div className="work-terms">
                            { work.acf.overview[0].used_skills.map(skill => (
                            <div className="work-term">{skill.name}</div>
                        ))}
                        </div>
                        <div className="button">
                            <a href={work.acf.overview[0].view_live} target="_blank" rel="noreferrer">View Live</a>
                        </div>
                    </div>

                    {/* Design Process */}
                    <div>
                        <h2>Design</h2>

                    </div>
                    <div>
                        <h2>Development</h2>
                        <div dangerouslySetInnerHTML={{ __html: work.content.rendered }}></div>
                    </div>
                </div>
            );
        }
        return <Loading/>
    }
}

export default WorkInfo;
