import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import axios from 'axios';

export class WorkItem extends Component {
    state = {
        imgUrl  : '',
        author  : '',
        termId  : [],
        categoriesInfo : null,
        isLoaded: false
    }

    static propTypes = {
        work: PropTypes.object.isRequired
    }

    componentDidMount() {
        const { featured_media, author, termId } = this.props.work;
        const getImageUrl = axios.get(`/wp-json/wp/v2/media/${featured_media}`);
        const getAuthor = axios.get(`/wp-json/wp/v2/users/${author}`);
        //const getTermName = axios.get(`/wp-json/wp/v2/custom_work_category/${termId}`);
        const getCategoryInfo = axios.get('/wp-json/wp/v2/custom_work_category') 

        Promise.all([getImageUrl, getAuthor]).then(res => {
            // console.log(res);
            this.setState({
                imgUrl  : res[0].data.media_details.sizes.full.source_url,
                author  : res[1].data.name,
                // term    : res[0].data.name,
                categoriesInfo: res[2].data,
                isLoaded: true
            });
        });
/*
        async function getCategoryInfo(){
            const res = await fetch('http://localhost:8888/portfolio/wp-json/wp/v2/custom_work_category');
            const data = await res.json();
            console.log(data);
            this.setState({
                categoriesInfo: data
            })
        }
        */

        //getCategoryInfo();


    }

    render() {
        const { id, slug, title, excerpt, custom_work_category } = this.props.work;
        const { author, imgUrl, isLoaded, categoriesInfo } = this.state;
        const termIdList = custom_work_category.map(termId => <li>{termId}</li>);
        console.log({categoriesInfo});


        if ( isLoaded ) {
            return (
                <div>
                    {/* // Page Work List */}

                    <Link to={`/work/${slug}`}><img src={imgUrl} alt={title.rendered} /></Link>
                    <h2>{ title.rendered }</h2>
                    <div dangerouslySetInnerHTML={{ __html: excerpt.rendered }}></div>
                    <Link to={`/work/${slug}`}>See Details</Link>
                    <div>{author}</div>
                    <div>{termIdList}</div>

                    {/* {custom_work_category.map((termId, i) => {
                        return (
                            <span className="work-terms" key={i}>{termId}<br/></span>
                        )
                    })} */}
                </div>
            );
        }
        return null;
    }
}

export default WorkItem
