import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import axios from 'axios';

export class WorkItem extends Component {
    state = {
        imgUrl  : '',
        author  : '',
        work    : '',
        isLoaded: false
    }

    static propTypes = {
        work: PropTypes.object.isRequired
    }

    componentDidMount() {
        const { featured_media, author } = this.props.work;
        const getImageUrl = axios.get(`/wp-json/wp/v2/media/${featured_media}`);
        const getAuthor = axios.get(`/wp-json/wp/v2/users/${author}`);

        Promise.all([getImageUrl, getAuthor]).then(res => {
            console.log(res);
            this.setState({
                imgUrl  : res[0].data.media_details.sizes.full.source_url,
                author  : res[1].data.name,
                isLoaded: true
            });
        });
    }

    render() {
        const { id, title, excerpt } = this.props.work;
        const { work, author, imgUrl, isLoaded } = this.state;

        if ( isLoaded ) {
            return (
                <div>
                    <img src={imgUrl} alt={title.rendered} />
                    <h2>{ title.rendered }</h2>
                    <small>Review by <strong>{author}</strong></small>
                    <div dangerouslySetInnerHTML={{ __html: excerpt.rendered }}></div>
                    <Link to={`/work/${id}`}>View Details</Link>
                    <h4>Skills: {work.acf.skills}</h4>

                </div>
            );
        }
        return null;
    }
}

export default WorkItem
