import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Loading from './Loading';
import ReactEmbedGist from 'react-embed-gist';


export class WorkPage extends Component {
    state = {
        work: {},
        isLoaded: false
    }

    componentDidMount() {
        let id ;
        switch(this.props.match.params.id) {
            case 'seoul-alleys':
              id = 13;
              break;
            case 'kine-movie':
                id = 11;
                break;
            case 'the-velou':
                id = 21;
                break;
            default: 
                id = null;
              // code block
        }

        axios.get(`/wp-json/wp/v2/works/${id}`)
        // axios.get(`/wp-json/wp/v2/works?slug=${this.props.match.params.slug}`)

        .then(res => this.setState({
            work: res.data,
            isLoaded: true
        }))
        .catch(err => console.log(err));
    }

    render() {
        const { work, isLoaded } = this.state;
        if ( isLoaded ) {
            return (
                <div>
                    <div className="work-hero">
                        <img src={work.acf.hero_bg_image} alt="Page hero" />
                        <h1>{work.title.rendered}</h1>
                    </div>
                    <div className="work-overview">
                        <h2>Overview</h2>
                        <p>{work.acf.overview[0].overview}</p>

                        { work.acf.overview[0].used_skills.map(skill => (
                        <div>{skill.name}</div>
                    ))}
                        <div className="used-skills">
                            {work.acf.overview[0].used_skills[0].name}
                        </div>

                        <div className="button">
                            <a href={work.acf.overview[0].view_live} target="_blank" rel="noreferrer">View Live</a>
                        </div>
                    </div>
                    <div dangerouslySetInnerHTML={{ __html: work.content.rendered }}></div>
                    <h4>Skills: {work.acf.skills}</h4>
                    <h4>Tools: {work.acf.tools}</h4>
                </div>
            );
        }
        return <Loading/>
    }
}

export default WorkPage;
