import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import axios from 'axios';

export class WorkItem extends Component {
    state = {
        imgUrl  : '',
        author  : '',
        isLoaded: false
    }

    static propTypes = {
        work: PropTypes.object.isRequired
    }

    componentDidMount() {
        const { featured_media, category } = this.props.work;
        const getImageUrl = axios.get(`/wp-json/wp/v2/media/${featured_media}`);

        Promise.all([getImageUrl]).then(res => {
            console.log(res);
            this.setState({
                imgUrl  : res[0].data.media_details.sizes.full.source_url,
                isLoaded: true
            });
        });
    }

    render() {
        const { id, slug, title, excerpt } = this.props.work;
        const { imgUrl, isLoaded } = this.state;

        if ( isLoaded ) {
            return (
                <div>
                    <Link to={`/work/${id}`}><img src={imgUrl} alt={title.rendered} /></Link>
                    <h2>{ title.rendered }</h2>
                    <div dangerouslySetInnerHTML={{ __html: excerpt.rendered }}></div>
                    <Link to={`/work/${slug}`}>See Details</Link>
                    {/* <h4>Skills: {work.acf.skills}</h4> */}
                </div>
            );
        }
        return null;
    }
}

export default WorkItem
