import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import axios from 'axios';
// import WorkTermList from './WorkTermList';

export class WorkItem extends Component {

    state = {
        imgUrl  : '',
        author: '',
        terms : [],
        isLoaded: false
    }

    static propTypes = {
        work: PropTypes.object.isRequired
    }

    componentDidMount() {
        const { featured_media, author, terms } = this.props.work;
        const getImageUrl = axios.get(`/wp-json/wp/v2/media/${featured_media}`);
        const getAuthor = axios.get(`/wp-json/wp/v2/users/${author}`);
        const getTerms = axios.get(`/wp-json/wp/v2/custom_work_category/${terms}`);

// POST MAN path: http://localhost:8888/portfolio/wp-json/wp/v2/custom_work_category/15

        Promise.all([getImageUrl, getAuthor, getTerms]).then(res => {
            console.log(res);
            console.log(terms[0]);
            this.setState({
                imgUrl  : res[0].data.media_details.sizes.full.source_url,
                author  : res[1].data.name,
                getTerms: res[0].data.name,
                isLoaded: true
            });
        });

    }

    render() {
        const { id, title, excerpt, custom_work_category } = this.props.work;
        const { imgUrl, author, terms, isLoaded } = this.state;


        if ( isLoaded ) {
            return (
                <div>
                    {/* // Page Work List */}
                    <Link to={`/work/${id}`}><img src={imgUrl} alt={title.rendered} /></Link>
                    <h2>{ title.rendered }</h2>
                    <div dangerouslySetInnerHTML={{ __html: excerpt.rendered }}></div>
                    <Link to={`/work/${id}`}>See Details</Link>
                    <div>{author}</div>

                    {custom_work_category.map((termId, i) => {
                        console.log('/wp-json/wp/v2/custom_work_category/20');
                        return (
                            <span className="work-terms" key={i}>{termId}<br/></span>
                        )
                    })}
                </div>
            );
        }
        return null;
    }
}

export default WorkItem
