import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import axios from 'axios';

export class WorkItem extends Component {

    state = {
        imgUrl  : '',
        workTerms : [],
        isLoaded: false
    }

    static propTypes = {
        work: PropTypes.object.isRequired
    }

    componentDidMount() {
        const { featured_media, taxonomy_id } = this.props.work;
        const getImageUrl = axios.get(`/wp-json/wp/v2/media/${featured_media}`);
        const getTaxonomyTerm = axios.get(`/wp-json/wp/v2/custom_work_category/${taxonomy_id}`);

// POST MAN path: http://localhost:8888/portfolio/wp-json/wp/v2/custom_work_category/15

        Promise.all([getImageUrl], [getTaxonomyTerm]).then(res => {
            console.log(workTerms);
            this.setState({
                imgUrl  : res[0].data.media_details.sizes.full.source_url,
                workTerms: res[0].data.name,
                isLoaded: true
            });
        });

        // Promise.all([getTaxonomyTerm]).then(res => {
        //     console.log(res);
        //     this.setState({
        //         workTerms: res[0].data.name,
        //         isLoaded: true
        //     })
        // })

    }

    render() {
        const { id, slug, title, excerpt, custom_work_category } = this.props.work;
        const { imgUrl, taxonomyTerm, isLoaded } = this.state;


        if ( isLoaded ) {
            return (
                <div>
                    {/* // Page Work List */}
                    <Link to={`/work/${id}`}><img src={imgUrl} alt={title.rendered} /></Link>
                    <h2>{ title.rendered }</h2>
                    <div dangerouslySetInnerHTML={{ __html: excerpt.rendered }}></div>
                    <Link to={`/work/${id}`}>See Details</Link>

                    {custom_work_category.map((term, i) => {
                        return (
                            <span className="work-terms" key={i}>{term}<br/></span>
                        )
                    })}
                </div>
            );
        }
        return null;
    }
}

export default WorkItem
