import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Loading from './Loading';
import ReactEmbedGist from 'react-embed-gist';


export class WorkPage extends Component {
    state = {
        work: {},
        isLoaded: false
    }

    componentDidMount() {
        axios.get(`/wp-json/wp/v2/works/${this.props.match.params.id}`)
        // axios.get(`/wp-json/wp/v2/works?slug=${this.props.match.params.slug}`)

        .then(res => this.setState({
            work: res.data,
            isLoaded: true
        }))
        .catch(err => console.log(err));
    }

    render() {
        const { work, isLoaded } = this.state;
        if ( isLoaded ) {
            return (
                <div>
                    <h2>{work.slug}</h2>
                    <Link to='/'>Go Back</Link>
                    <h1>{work.title.rendered}</h1>
                    <div dangerouslySetInnerHTML={{ __html: work.content.rendered }}></div>
                    <h4>Skills: {work.acf.skills}</h4>
                    <h4>Tools: {work.acf.tools}</h4>

                    <script src="https://gist.github.com/Jihye-uxui/cf9f89435b6bd092cae99f90cc036f6b.js"></script>

                    <ReactEmbedGist gist="Jihye-uxui/cf9f89435b6bd092cae99f90cc036f6b"
                    wrapperClass="gist__bash"
                    loadingClass="loading__screen"
                    titleClass="gist__title"
                    contentClass="gist__content"
                    errorClass="gist__error"
                    file=".bash_profile.sh"/>
                </div>
            );
        }
        return <Loading/>
    }
}

export default WorkPage;
